import React, { useState } from "react";
import "./All.css";

const All = () => {
  const [Categorie, setCategorie] = useState([
    { id: 1, categorie: 'A' },
    { id: 2, categorie: 'B' },
  ]);
  const [Etat, setEtat] = useState([
    { id: 1, etat: 'neuf' },
    { id: 2, etat: 'bien' },
  ]);

  const [newCategorie, setNewCategorie] = useState('');
  const [newCategorieEtat, setNewCategorieEtat] = useState('');

  const handleAdd = (type) => {
    // Vérifier si le champ n'est pas vide
    if (type === 'categorie' && newCategorie.trim() === '') {
      alert('Champ de catégorie vide.');
      return;
    } else if (type === 'etat' && newCategorieEtat.trim() === '') {
      alert('Champ d\'état vide.');
      return;
    }

    // Générer un nouvel ID (supposant que les IDs sont des nombres entiers)
    const newId = Math.max(...(type === 'categorie' ? Categorie : Etat).map((item) => item.id), 0) + 1;

    // Ajouter la nouvelle entrée à la liste correspondante
    const newData = type === 'categorie'
      ? [...Categorie, { id: newId, categorie: newCategorie }]
      : [...Etat, { id: newId, etat: newCategorieEtat }];

    type === 'categorie' ? setCategorie(newData) : setEtat(newData);

    // Réinitialiser le champ de saisie
    type === 'categorie' ? setNewCategorie('') : setNewCategorieEtat('');
  };

  return (
    <div className="container">
      <h1 className="heading">Listes des Categories</h1>

      <div id="categorie">
        <table>
          <thead>
            <tr>
              <th>Numero</th>
              <th>Categorie</th>
            </tr>
          </thead>
          <tbody>
            {Categorie.map((row) => (
              <tr key={row.id}>
                <td>{row.id}</td>
                <td>{row.categorie}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="add-category">
          <input
            type="text"
            placeholder="Nouvelle catégorie"
            value={newCategorie}
            onChange={(e) => setNewCategorie(e.target.value)}
            className="category-input"
          />
          <button onClick={() => handleAdd('categorie')} className="add-button">
            + Ajouter
          </button>
        </div>
      </div>

      <div id="etat">
        <table>
          <thead>
            <tr>
              <th>Numero</th>
              <th>Etat</th>
            </tr>
          </thead>
          <tbody>
            {Etat.map((row) => (
              <tr key={row.id}>
                <td>{row.id}</td>
                <td>{row.etat}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="add-etat">
          <input
            type="text"
            placeholder="Nouvelle Etat"
            value={newCategorieEtat}
            onChange={(e) => setNewCategorieEtat(e.target.value)}
            className="etat-input"
          />
          <button onClick={() => handleAdd('etat')} className="add-button">
            + Ajouter
          </button>
        </div>
      </div>
    </div>
  );
};

export default All;
