import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import "chart.js/auto"; // Assurez-vous d'importer "chart.js/auto" pour inclure toutes les échelles
import "./Statistique.css";

const Statistique = () => {
  // Exemple de données
  const [key, setKey] = useState(0); // Ajout de l'état pour gérer la clé

  const data = {
    labels: ["Janvier", "Février", "Mars", "Avril", "Mai"],
    datasets: [
      {
        label: "Nombre d'utilisateurs",
        data: [120, 150, 200, 180, 220],
        backgroundColor: "rgba(75,192,192,0.2)",
        borderColor: "rgba(75,192,192,1)",
        borderWidth: 1,
      },
    ],
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  const refreshChart = () => {
    setKey((prevKey) => prevKey + 1); // Mettez à jour la clé pour forcer la réinitialisation du composant
  };

  return (
    <div className="statistique-container">
      <div className="chart-container" onClick={refreshChart}>
        <Bar key={key} data={data} options={options} />
      </div>
    </div>
  );
};

export default Statistique;
