import React, { useState } from "react";
import "./All.css";

const All = () => {
  const [Categorie, setCategorie] = useState([
    { id: 1, categorie: 'A' },
    { id: 2, categorie: 'B' },
  ]);

  const [newCategorie, setNewCategorie] = useState('');

  const handleAdd = () => {
    // Vérifier si la nouvelle catégorie n'est pas vide
    if (newCategorie.trim() === '') {
      alert('Veuillez saisir une catégorie.');
      return;
    }

    // Générer un nouvel ID (supposant que les IDs sont des nombres entiers)
    const newId = Math.max(...Categorie.map((item) => item.id), 0) + 1;

    // Ajouter la nouvelle catégorie à la liste
    const newCategorieData = [...Categorie, { id: newId, categorie: newCategorie }];
    setCategorie(newCategorieData);

    // Réinitialiser le champ de saisie
    setNewCategorie('');
  };

  return (
    <div className="container">
      <h1 className="heading">Listes des Categories</h1>
      <div id="categorie">
        <table>
          <thead>
            <tr>
              <th>Numero</th>
              <th>Categorie</th>
            </tr>
          </thead>
          <tbody>
            {Categorie.map((row) => (
              <tr key={row.id}>
                <td>{row.id}</td>
                <td>{row.categorie}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="add-category">
          <input
            type="text"
            placeholder="Nouvelle catégorie"
            value={newCategorie}
            onChange={(e) => setNewCategorie(e.target.value)}
            className="category-input"
          />
          <button onClick={handleAdd} className="add-button">
            + Ajouter
          </button>
        </div>
      </div>
    </div>
  );
};

export default All;
